const express = require('express');
const bodyParser = require('body-parser');
const sqlite3 = require('sqlite3').verbose();
const axios = require('axios');

const app = express();
const port = 5000;

// Middleware
app.use(bodyParser.json());

// Database setup
const db = new sqlite3.Database(':memory:');
db.serialize(() => {
  db.run("CREATE TABLE forms (id INTEGER PRIMARY KEY AUTOINCREMENT, formType TEXT, name TEXT, countryCode TEXT, phoneNumber TEXT)");
});

// Routes
app.post('/submit-form', (req, res) => {
  const { formType, name, countryCode, phoneNumber } = req.body;
  const stmt = db.prepare("INSERT INTO forms (formType, name, countryCode, phoneNumber) VALUES (?, ?, ?, ?)");
  stmt.run(formType, name, countryCode, phoneNumber, (err) => {
    if (err) {
      res.status(500).send(err.message);
    } else {
      res.status(200).send('Form submitted successfully');
    }
  });
  stmt.finalize();
});

app.get('/refresh-data', async (req, res) => {
  db.all("SELECT * FROM forms", async (err, rows) => {
    if (err) {
      res.status(500).send(err.message);
      return;
    }


    try {
      const sheetId = 'your-google-sheet-id';
      const apiKey = 'your-google-sheets-api-key';

      const response = await axios.put(`https://sheets.googleapis.com/v4/spreadsheets/${sheetId}/values/Sheet1!A1:D1:append?valueInputOption=USER_ENTERED&key=${'AIzaSyA--rPS1n1jhsk5Sl2S1-Y9CLTJROtiWwA'}`, {
        range: 'Sheet1!A1:D1',
        majorDimension: 'ROWS',
        values: rows.map(row => [row.formType, row.name, row.countryCode, row.phoneNumber])
      });

      res.status(200).send('Data synchronized successfully');
    } catch (error) {
      res.status(500).send(error.message);
    }
  });
});

app.listen(port, () => {
  console.log(`Server is running on http://localhost:${port}`);
});
